def clean_html_to_text(html_content: str) -> str:
    """
    Takes raw HTML or plain text and returns cleaned text content.
    """
    if not html_content.strip():
        return ""

    # If it looks like plain text (no HTML tags), just normalize
    if "<" not in html_content and ">" not in html_content:
        return " ".join(html_content.split())

    # Otherwise treat it as HTML
    soup = BeautifulSoup(html_content, "html.parser")

    # Remove scripts, styles, metadata, etc.
    for tag in soup(["script", "style", "noscript", "meta", "link", "head"]):
        tag.decompose()

    # Extract visible text
    text = soup.get_text(separator=" ", strip=True)

    return " ".join(text.split())

